import './post.css';
import img1 from './images/1.jpeg';
// import mops from './images/mops.jpeg'
import likes from './images/like.png';
import heart from './images/hearth.png';
import { MoreVert } from '@mui/icons-material';
import { Users } from '../../dummyData';
import { useState } from 'react';
import axios from 'axios';

export default function Post({ post }) {
    const [like, setLike] = useState(post.like);
    const [isLike, setIsLike] = useState(false);
    const [user, setUser] = useState({});
    const PF = process.env.REACT_APP_PUBLIC_FOLDER;

    useEffect(() => {
        const fetchUser = async () => {
            const res = await axios.get(`users/${post.userId}`);
            setUser(res.data);
        };
        fetchUser();
    }, []);

    const likeHandler = () => {
        setLike(isLike ? like - 1 : like + 1);
        setIsLike(!isLike);
    };

    return (
        <div className='post'>
            <div className='postWrapper'>
                <div className='postTop'>
                    <div className='postTopLeft'>
                        <img
                            src={user.profilePictu}
                            alt=''
                            className='postProfileImg'
                        />
                        <span className='postUserName'>
                            {
                                Users.filter((u) => u.id === post?.userId)[0]
                                    .username
                            }
                        </span>

                        <span className='postDate'>{post.date}</span>
                    </div>
                    <div className='postTopRight'>
                        <MoreVert />
                    </div>
                </div>
                <div className='postCenter'>
                    <span className='postText'>{post?.desc}</span>
                    <img src={PF + post.photo} className='postImg' alt='' />
                </div>
                <div className='postBottom'>
                    <div className='postBottomLeft'>
                        <img
                            className='likeIcon'
                            src={`${PF}like.png`}
                            alt=''
                            onClick={likeHandler}
                        />
                        <img
                            className='likeIcon'
                            src={`${PF}hearth.png`}
                            alt=''
                            onClick={likeHandler}
                        />
                        <span className='postLikeCounter'>{like}</span>
                    </div>
                    <div className='postBottomRight'>
                        <span className='postCommentText'>
                            {post.comment} комментариев
                        </span>
                    </div>
                </div>
            </div>
        </div>
    );
}
